mapper.select:
select su.id,tb.isbn,tb.sm,tb.cbsmc,tb.cbrq,tb.zz,tb.lxmc,tb.tsdl,su.xm uname,su.wz,su.sh,su.rksj,su.status,tb.jj,tb.picture from ( select s.*,u.xm from t_store s left join t_sys_user u on s.user_id = u.user_id ) su left join ( select b.*,t.lxmc from t_book b left join t_type t on b.type_id = t.type_id
) tb on su.isbn = tb.isbn
where tb.sm like '%${dim}%' or tb.zz like '%${dim}%'
limit #{start},${end}

select count(*)
from t_comment c LEFT JOIN t_sys_user u on c.user_id = u.user_id

    WHERE c.isbn = ${dim}

select u.xm,c.pjrq,c.nr,c.comment_id commentId
from t_comment c LEFT JOIN t_sys_user u on c.user_id = u.user_id
where c.comment_id = #{commentId}

select u.xm,c.pjrq,c.nr,c.comment_id commentId
from t_comment c LEFT JOIN t_sys_user u on c.user_id = u.user_id

    WHERE c.isbn = ${dim}

limit #{start},${end}
