WHERE n.isbn=b.isbn AND p.type_id=b.type_id
AND n.id = #{id,jdbcType=INTEGER}

SELECT count(*) FROM
(SELECT ts.isbn,e.* FROM
(
SELECT s.xm,t.* FROM t_borrow t LEFT JOIN t_sys_user s ON t.user_id = s.user_id
) e,t_store ts
